*
* Author: Joshua Bijak
* Description: Smart contract for monitoring and controlling states of a LED DAlI enabled Creol Luminaire
* TODO: Check and validate all inputs coming into functions
*/


import  '../../node_modules/openzeppelin-solidity/contracts/ownership/Ownable.sol';




contract LEDState is Ownable {

    //On off switch
    bool public currentState;

    uint256 public daliShortAddress;
    //On how much, if off. Remember this dim rate
    uint256 public LEDDim =0;

    address public roomContract;


    event daliShortAddressUpdated(uint256 daliShortAddress);
    event stateUpdated(bool updatedState);
    event roomChanged(address roomContract);
    event dimUpdated(uint256 LEDDim);

    constructor(bool defaultState, address _roomContract,address _roomOwner, uint256 _daliShortAddress, uint256 _LEDDim) public{
        // TODO: Check inputs and validate, errors here come from Instantiation Abuse
        roomContract = _roomContract;

        daliShortAddress = _daliShortAddress;
        emit daliShortAddressUpdated(daliShortAddress);

        currentState = defaultState;
        //TODO: Check that LEDDim is between range of 0 -253
        LEDDim = _LEDDim;

        emit stateUpdated(defaultState);
        emit dimUpdated(LEDDim);

        transferOwnership(_roomOwner);


    }
    function onDim(bool newState, uint256 _LEDDim) public onlyOwner{
        updateState(newState);
        updateDim(_LEDDim);
    }


    function updateState(bool newState) public onlyOwner{
        currentState = newState;
        emit stateUpdated(currentState);
    }
    function updateDim(uint256 _LEDDim) public onlyOwner{
        LEDDim = _LEDDim;
        emit dimUpdated(_LEDDim);
    }

    function updatedaliShortAddr(uint256 newdaliShortAddr) public onlyOwner{
        daliShortAddress = newdaliShortAddr;
        emit daliShortAddressUpdated(daliShortAddress);
    }
    function updateRoomContract(address newRoomAddr) public onlyOwner{
        roomContract = newRoomAddr;
        emit roomChanged(roomContract);
    }


}
